import React, { useEffect, useState } from "react";
import PodcastCard from "../../creator/components/Podcast_card.tsx";
import Button from "../../../shared/components/Button.tsx";
import { useToast } from "../../../shared/components/Toast.js";
import { podcastController } from "../controller/podcast_controller.tsx";
import { useNavigate } from "react-router-dom";
import CreatePodcastModal from "../components/Create_podcast.tsx";

const PodcastPage = () => {
  const { fetchPodcasts, podcasts, isLoading, isError, errorMessage } =
    podcastController();
  const [open, setOpen] = useState<boolean>(false);

  const { error } = useToast();
  const navigate = useNavigate();

  useEffect(() => {
    if (!podcasts || podcasts?.data?.length === 0) {
      fetchPodcasts();
    }
  }, [fetchPodcasts, podcasts]);

  if (isLoading) {
    return <div>Loading podcasts...</div>;
  }

  if (isError) {
    error(errorMessage || "Failed to load podcasts");
  }

  return (
    <div>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "20px",
        }}
      >
        <h1 className='text-2xl font-bold'>My Podcasts</h1>

        <Button
          color='primary'
          type='button'
          fullWidth={false}
          onClick={() => setOpen(true)}
        >
          Create Podcast
        </Button>
      </div>

      <CreatePodcastModal open={open} onClose={() => setOpen(false)} />

      {podcasts &&
      podcasts!.page_metadata &&
      podcasts!.page_metadata!.length > 0 ? (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(350px, 1fr))",
            gap: "20px",
          }}
        >
          {podcasts!.data!.map((podcast) => (
            <PodcastCard
              key={podcast.id}
              image={podcast.image_url}
              title={podcast.title}
              description={podcast.description}
              createdAt={new Date(podcast.created_at!).toLocaleDateString()}
              audioSrc={podcast.audio_url}
            />
          ))}
        </div>
      ) : (
        <div>No podcasts found. Create your first podcast!</div>
      )}
    </div>
  );
};

export default PodcastPage;

